mapper.select:
SELECT no, userid, groupname, memo, region, DATE_FORMAT(targetdate, '%Y.%c.%e') as targetdate, color
FROM memo_tb


        WHERE userid = #{userid}
        AND   groupname is null


        WHERE groupname = #{groupname}


        AND   region = #{region}

SELECT no, userid, groupname, memo, region, DATE_FORMAT(targetdate, '%Y.%c.%e') as targetdate, color
FROM memo_tb


        WHERE userid = #{userid}
        AND   groupname is null


        WHERE groupname = #{groupname}


ORDER BY no desc

mapper.delete:
DELETE FROM memo_tb
WHERE userid = #{userid}
AND   no   = #{no}

mapper.update:
UPDATE memo_tb
SET    memo = #{newMemo}
WHERE  userid  = #{userid}
AND    no      = #{no}
